double N1;
double N2;
double R;
char OP;
private void SetNum(String s){
    String All= EntNum.getText()+s;
    EntNum.setText(All);
}
private void SetOp(char Op){
    N1=Double.parseDouble(EntNum.getText());
    EntNum.setText("");
    OP=Op;
} 
