    private void EqualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_EqualActionPerformed
       N2=Double.parseDouble(EntNum.getText());
       switch(OP){
           case '+':R=N1+N2;break;
            case '-':R=N1-N2;break;
             case '*':R=N1*N2;break;
              case '/':R=N1/N2;break;
       }
       EntNum.setText(String.valueOf(R));
    }//GEN-LAST:event_EqualActionPerformed

    private void ChangeOpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ChangeOpActionPerformed
        double num= Double.parseDouble(EntNum.getText());
        num=num*(-1);
        EntNum.setText(String.valueOf(num));
    }//GEN-LAST:event_ChangeOpActionPerformed

    private void DeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteActionPerformed
       int l = EntNum.getText().length();
       int i= EntNum.getText().length()-1;
       String stor;
       if(l>0){
       StringBuilder back = new StringBuilder(EntNum.getText());
           back.deleteCharAt(i);
           stor=back.toString();
           EntNum.setText(stor);
       }
    }//GEN-LAST:event_DeleteActionPerformed

